import React, { Component } from "react";
import SimpleStorageContract from "./contracts/ContractReg.json";
import getWeb3 from "./getWeb3";

import "./App.css";

import bootstrap from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';


class App extends Component {
  state = { storageValue: 0, web3: null, accounts: null, contract: null };

  componentDidMount = async () => {
    try {
      // Get network provider and web3 instance.
      const web3 = await getWeb3();

      // Use web3 to get the user's accounts.
      const accounts = await web3.eth.getAccounts();

      // Get the contract instance.
      const networkId = await web3.eth.net.getId();
      const deployedNetwork = SimpleStorageContract.networks[networkId];
      const instance = new web3.eth.Contract(
        SimpleStorageContract.abi,
        deployedNetwork && deployedNetwork.address,
      );

      // Set web3, accounts, and contract to the state, and then proceed with an
      // example of interacting with the contract's methods.
      this.setState({ web3, accounts, contract: instance }, this.runExample);
    } catch (error) {
      // Catch any errors for any of the above operations.
      alert(
        `Failed to load web3, accounts, or contract. Check console for details.`,
      );
      console.error(error);
    }
  };

  runExample = async () => {
    const { accounts, contract } = this.state;

    // Stores a given value, 5 by default.
    //await contract.methods.set(5).send({ from: accounts[0] });

    // Get the value from the contract to prove it worked.
    // const response = await contract.methods.get().call();

    // Update state with the result.
    // this.setState({ storageValue: response });
  };

  render() {
    if (!this.state.web3) {
      return <div>Loading Web3, accounts, and contract...</div>;
    }
    return (
      <div className="App">
        <div className="container">
          <div className="row">
            <div className="col-lg-12">
              <h1>Welcome to the second use case session!</h1>
              <div className="form-group">

                <form>

                  <label htmlFor="contractid">Contract ID</label>
                  <input className="form-control" name="contractid" placeholder="contractid"  ></input>
                  <label htmlFor="ownername">Owner Name</label>
                  <input className="form-control" name="ownername" placeholder="Owner Name"  ></input>
                  <label htmlFor="contractdesc">Contract Description</label>
                  <input className="form-control" name="contractdesc" placeholder="Contract Description"  ></input>

                  <label htmlFor="contracttype">Contract Type</label>
                  <select className="form-control">
                    <option value="1">Land Contract</option>
                    <option value="2">Home Contract</option>
                    <option value="3">Shop Contract</option>
                    <option value="4">Building Contract</option>
                  </select>

                  <label htmlFor="municipalities">Municipality</label>

                  <select className="form-control"  name="municipalities" >
                    <option value="1">Khan Younes</option>
                    <option value="2">Deir Al Balah</option>
                    <option value="3">Gaza</option>
                    <option value="4">Rafah</option>
                    <option value="5">Jenin</option>
                    <option value="6">Nablus</option>
                    <option value="7">Jerusalem</option>
                    <option value="8">Haifa</option>
                    <option value="9">Acre</option>
                  </select>

                  <label htmlFor="startdate">Start Date</label>
                  <input className="form-control" name="startdate" placeholder="Start Date "  ></input>

                  <label htmlFor="enddate">End Date</label>
                  <input className="form-control" name="enddate" placeholder="End Date"  ></input>

                  <label htmlFor="contractimage">Contract Image</label>
                  <input type="file" className="form-control" name="contractimage" placeholder="Contract Image"  ></input>

                  <input type="submit" className=""  ></input>
                </form>
              </div>

            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default App;
